from langflow.custom.custom_component.component import Component
from langflow.io import MessageInput, Output
from langflow.schema.message import Message

class RouterComponent(Component):
    display_name = "Smart Router"
    description = "Routes messages to appropriate agents based on content"
    icon = "GitBranch"

    inputs = [
        MessageInput(
            name="user_message",
            display_name="User Message", 
            info="The user's message to route",
            required=True
        )
    ]

    outputs = [
        Output(display_name="General Route", name="general_route", method="route_to_general"),
        Output(display_name="Product Route", name="product_route", method="route_to_product"), 
        Output(display_name="Technical Route", name="technical_route", method="route_to_technical")
    ]

    def determine_route(self, message_text: str) -> str:
        """Determine which agent should handle the message"""
        message = message_text.lower()

        # Product keywords
        product_keywords = [
            'product', 'price', 'cost', 'laptop', 'computer', 'specification', 'specs', 
            'feature', 'model', 'gaming', 'ultra', 'pro', 'ram', 'storage', 'memory',
            'processor', 'cpu', 'gpu', 'display', 'screen', 'battery', 'performance',
            'comparison', 'compare', 'recommend', 'best', 'which', 'availability',
            'stock', 'sale', 'discount', 'offer'
        ]

        # Technical keywords  
        technical_keywords = [
            'setup', 'install', 'configure', 'troubleshoot', 'error', 'problem',
            'issue', 'bug', 'crash', 'freeze', 'slow', 'driver', 'update',
            'software', 'firmware', 'reset', 'repair', 'fix', 'broken',
            'malfunction', 'technical', 'support', 'help', 'manual', 'guide',
            'tutorial', 'how to', 'connection', 'network', 'wifi', 'bluetooth'
        ]

        # General keywords (policies, shipping, returns)
        general_keywords = [
            'return', 'refund', 'exchange', 'warranty', 'shipping', 'delivery',
            'order', 'payment', 'policy', 'account', 'login', 'register',
            'tracking', 'status', 'cancel', 'modify', 'customer service',
            'contact', 'hours', 'location', 'store', 'policy'
        ]

        # Count matches for each category
        product_matches = sum(1 for keyword in product_keywords if keyword in message)
        technical_matches = sum(1 for keyword in technical_keywords if keyword in message) 
        general_matches = sum(1 for keyword in general_keywords if keyword in message)

        # Determine route based on highest matches
        if product_matches > technical_matches and product_matches > general_matches:
            return "product"
        elif technical_matches > general_matches:
            return "technical" 
        else:
            return "general"

    def get_message_text(self) -> str:
        """Extract text content from Message object"""
        if hasattr(self.user_message, 'text'):
            return self.user_message.text
        elif isinstance(self.user_message, str):
            return self.user_message
        else:
            return str(self.user_message)

    def route_to_general(self) -> Message:
        message_text = self.get_message_text()
        route = self.determine_route(message_text)

        if route == "general":
            self.status = f"✅ Routing to General Support: {message_text[:50]}..."
            return Message(text=message_text, properties={"agent_type": "general"})
        else:
            # Return None or empty message if not routed to this agent
            self.status = "❌ Not routed to General Support"
            return Message(text="", properties={"agent_type": "skip"})

    def route_to_product(self) -> Message:
        message_text = self.get_message_text()
        route = self.determine_route(message_text)

        if route == "product":
            self.status = f"✅ Routing to Product Specialist: {message_text[:50]}..."
            return Message(text=message_text, properties={"agent_type": "product"})
        else:
            self.status = "❌ Not routed to Product Specialist"
            return Message(text="", properties={"agent_type": "skip"})

    def route_to_technical(self) -> Message:
        message_text = self.get_message_text()
        route = self.determine_route(message_text)

        if route == "technical":
            self.status = f"✅ Routing to Technical Support: {message_text[:50]}..."
            return Message(text=message_text, properties={"agent_type": "technical"})
        else:
            self.status = "❌ Not routed to Technical Support"
            return Message(text="", properties={"agent_type": "skip"})

